import { React, useRef } from "react";
import { AllProducts, categories } from "../data";
import styled from "styled-components";
import { useParams } from "react-router-dom";
import { sliderItems } from "../data";

const Container = styled.div`
  backgroud-color: black;
  padding: 20px;
  text-align: center;
  display: grid;
  grid-template-columns: repeat(4, 1fr);
  grid-gap: 10px;
  width: 50px;
  border-color: black;
`;
const Wrapper = styled.div`
  background-color: white;
`;

const Image = styled.img`
  height: 400px;
  width: 300px;
`;

const ImageInfo = styled.div`
  text-align: left;
  padding-left: 20px;
`;

const Button = styled.button`
  border-radius: 7px;
  cursor: pointer;
  background-color: transparent;
`;

const Name = styled.p``;

const Price = styled.p``;

const Sizes = styled.p``;

const Size = styled.button``;

const Items = () => {
  const { id } = useParams();
  const receivedId = useRef(parseInt(id));
  const category = useRef(null);

  if (receivedId.current <= 3 && receivedId.current > 0) {
    sliderItems.forEach((ele) => {
      if (receivedId.current == ele.id) {
        category.current = ele.keyword;
      }
    });
  } else if (receivedId.current > 3) {
    categories.forEach((e) => {
      if (receivedId.current == e.id) {
        category.current = e.keyword;
      }
    });
  }

  function handleClick() {
    alert("button clicked");
  }

  switch (category.current) {
    case "Jacket":
      return AllProducts.Jacket.map((e) => {
        return (
          <Container>
            <Wrapper>
              <Image src={e.src} />
              <ImageInfo>
                <Name>Jacket</Name>
                <Price>Price: {e.price}</Price>
                <Button onClick={<></>}>Shop Now</Button>
              </ImageInfo>
            </Wrapper>
          </Container>
        );
      });
      break;
    case "Jeans":
      return AllProducts.Jeans.map((e) => {
        return (
          <Container>
            <Wrapper>
              <Image src={e.src} />
              <ImageInfo>
                <Name>Jacket</Name>
                <Price>Price: {e.price}</Price>
                <Button>Shop Now</Button>
              </ImageInfo>
            </Wrapper>
          </Container>
        );
      });
      break;
    case "Dress":
      return AllProducts.Dress.map((e) => {
        return (
          <Container>
            <Wrapper>
              <Image src={e.src} />
              <ImageInfo>
                <Name>Jacket</Name>
                <Price>Price: {e.price}</Price>
                <Button>Shop Now</Button>
              </ImageInfo>
            </Wrapper>
          </Container>
        );
      });
      break;
    default:
      alert("NO SUCH ITEM AVAILABLE");
  }
};

export default Items;
