import { React, useRef } from "react";
import { AllProducts, categories } from "../data";
import styled from "styled-components";
import { BrowserRouter, Routes, Route, Link } from "react-router-dom";
import { useParams } from "react-router-dom";
import { sliderItems } from "../data";
import Product from "./Product";
import Navbar from "./Navbar";
import Footer from "./Footer";
import CartContext from "../CartContext";
const Container = styled.div`
  backgroud-color: black;
  padding: 20px;
  text-align: center;
  display: grid;
  margin-top: 5px;
  grid-template-columns: repeat(3, 1fr);
  grid-gap: 400px;
  grid-row-gap: 30px;
  margin-left: 20px;
  width: 50px;
  border-color: black;
`;
const Wrapper = styled.div`
  background-color: white;
  width: 50px;
`;

const ImgContainer = styled.div``;

const Image = styled.img`
  height: 400px;
  width: 300px;
  border-style: solid;
  border-color: black;
`;

const ImageInfo = styled.div`
  padding-left: 20px;
`;

const Button = styled.button`
  border-radius: 7px;
  cursor: pointer;
  height: 40px;
  background: transparent;
  width: 200px;
`;

const Heading = styled.div`
  white-space: normal;
`;

const Name = styled.p`
  display: inline-block;
`;

const Price = styled.p``;

const Items = () => {
  const { id } = useParams();
  const receivedId = useRef(parseInt(id));
  const category = useRef(null);

  if (receivedId.current <= 3 && receivedId.current > 0) {
    sliderItems.forEach((ele) => {
      if (receivedId.current == ele.id) {
        category.current = ele.keyword;
      }
    });
  } else if (receivedId.current > 3) {
    categories.forEach((e) => {
      if (receivedId.current == e.id) {
        category.current = e.keyword;
      }
    });
  }

  //const Data = AllProducts.Jacket || AllProducts.Jeans || AllProducts.Dress;

  const Data = AllProducts[category.current + ""];

  return (
    <>
      <Navbar />

      <Container>
        {Data.map((e) => {
          return (
            <Wrapper>
              <ImgContainer>
              
              </ImgContainer>
              
              <ImageInfo>
                <Heading>
                  <Name>{e.name}</Name>
                </Heading>
                <Price>Price: {e.price}</Price>
                <Link to={`/product/${e.id}/${category.current}`}>
                  <Button>SHOP NOW</Button>
                </Link>
              </ImageInfo>
            </Wrapper>
          );
        })}
      </Container>

      <Footer />
    </>
  );
};

export default Items;
