import { React, useRef } from "react";
import { AllProducts, categories } from "../data";
import styled from "styled-components";
import { useParams } from "react-router-dom";
import { sliderItems } from "../data";

const Container = styled.div`
  backgroud-color: black;
  padding: 20px;
  text-align: center;
  display: grid;
  grid-template-columns: repeat(4, 1fr);
  grid-gap: 10px;
`;
const Wrapper = styled.div`
  background-color: white;
`;

const Image = styled.img`
  height: 400px;
  width: 300px;
`;

const ImageInfo = styled.div`
  text-align: left;
  padding-left: 20px;
`;

const Name = styled.p``;

const Price = styled.p``;

const Sizes = styled.p``;

const Size = styled.button``;

const Items = () => {
  const { id } = useParams();
  const receivedId = useRef(parseInt(id));
  const category = useRef(null);

  if (receivedId.current <= 3 && receivedId.current > 0) {
    sliderItems.forEach((ele) => {
      if (receivedId.current == ele.id) {
        category.current = ele.keyword;
      }
    });
  } else if (receivedId.current > 3) {
    categories.forEach((e) => {
      if (receivedId.current == e.id) {
        category.current = e.keyword;
      }
    });
  }

  switch (category.current) {
    case Jacket:
      return AllProducts.Jeans.map((e) => {
        return (
          <Container>
            <Wrapper>
              <Image src={e.src} />
              <ImageInfo>
                <Name>Jacket</Name>
                <Price>Price: {e.price}</Price>
                <Sizes>
                  Sizes :{" "}
                  {e.size.map((ele) => {
                    return <Size>{ele}</Size>;
                  })}
                </Sizes>
              </ImageInfo>
            </Wrapper>
          </Container>
        );
      });
      break;
    case Jeans:
      return AllProducts.Jeans.map((e) => {
        return (
          <Container>
            <Wrapper>
              <Image src={e.src} />
              <ImageInfo>
                <Name>Jacket</Name>
                <Price>Price: {e.price}</Price>
                <Sizes>
                  Sizes :{" "}
                  {e.size.map((ele) => {
                    return <Size>{ele}</Size>;
                  })}
                </Sizes>
              </ImageInfo>
            </Wrapper>
          </Container>
        );
      });
  }

  return (
    <Container>
      {AllProducts.Jeans.map((e) => {
        return (
          <Wrapper>
            <Image src={e.src} />
            <ImageInfo>
              <Name>Jacket</Name>
              <Price>Price: {e.price}</Price>
              <Sizes>
                Sizes :{" "}
                {e.size.map((ele) => {
                  return <Size>{ele}</Size>;
                })}
              </Sizes>
            </ImageInfo>
          </Wrapper>
        );
      })}
    </Container>
  );
};

export default Items;
