import { Add, Remove } from "@mui/icons-material";
import styled from "styled-components";
import Announcement from "../components/Announcement";
import Footer from "../components/Footer";
import Navbar from "../components/Navbar";
import { BrowserRouter, Routes, Route, Link } from "react-router-dom";
import { useParams } from "react-router-dom";
import { useEffect, useRef, useState } from "react";
import { AllProducts } from "../data";
import Newsletter from "../components/Newsletter";
const Container = styled.div``;

const Wrapper = styled.div`
  padding: 20px;
  display: flex;
`;

const Title = styled.h1`
  font-weight: 300;
  text-align: center;
`;

const Top = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 20px;
`;

const TopButton = styled.button`
  padding: 10px;
  font-weight: 600;
  cursor: pointer;
  border: ${(props) => props.type === "filled" && "none"};
  background-color: ${(props) =>
    props.type === "filled" ? "black" : "transparent"};
  color: ${(props) => props.type === "filled" && "white"};
`;

const TopTexts = styled.div``;
const TopText = styled.span`
  text-decoration: underline;
  cursor: pointer;
  margin: 0px 10px;
`;

const Bottom = styled.div`
  display: flex;
  justify-content: space-between;
`;

const Info = styled.div`
  display: flex;
`;

const Product = styled.div`
  display: flex;
  justify-content: space-between;
`;

const ProductDetail = styled.div``;

const Image = styled.img`
  width: 200px;
`;

const Details = styled.div`
  padding: 20px;
  display: flex;
  flex-direction: column;
  justify-content: space-around;
`;

const ProductName = styled.span``;

const ProductId = styled.span``;

const ProductSize = styled.span``;

const PriceDetail = styled.div`
  flex: 2;
  align-items: center;
  justify-content: center;
`;

const ProductAmountContainer = styled.div`
  display: flex;
  align-items: center;
  margin-bottom: 20px;
`;

const ProductAmount = styled.div`
  font-size: 24px;
  margin: 5px;
`;

const ProductPrice = styled.div`
  font-size: 30px;
  font-weight: 200;
`;

const Summary = styled.div`
  position: absolute;
  right: 0;
  margin-right: 10px;
  border: 0.5px solid lightgray;
  border-radius: 10px;
  padding: 20px;
  height: 50vh;
`;

const SummaryTitle = styled.h1`
  font-weight: 200;
`;

const SummaryItem = styled.div`
  margin: 30px 0px;
  display: flex;
  justify-content: space-between;
  font-weight: ${(props) => props.type === "total" && "500"};
  font-size: ${(props) => props.type === "total" && "24px"};
`;

const SummaryItemText = styled.span``;

const SummaryItemPrice = styled.span``;

const Button = styled.button`
  width: 100%;
  padding: 10px;
  background-color: black;
  color: white;
  font-weight: 600;
`;

const Cart = () => {

  use



  const { id, name } = useParams();

  const [Items, setItems] = useState([]);
  const [Total, setTotal] = useState(0);

  const Data = AllProducts[name];

  const ship = 299;
  let item = {};

  const Add = (item, price) => {
    const newProduct = [...Items, item];
    setItems(newProduct);
    let Price = parseInt(price);
    Price = Price + Total;
    setTotal(Price);
  };

  useEffect(() => {
    try {
      Data.forEach((e) => {
        if (e.id == id) {
          item = {
            itemName: e.name,
            price: e.price,
            src: e.src,
          };
          Add(item, e.price);
        }
      });
    } catch (e) {
      console.log(e);
    }
  }, []);

  return (
    <Container>
      <Title>YOUR BAG</Title>
      <Top>
        <Link to={`/`}>
          <TopButton>CONTINUE SHOPPING</TopButton>
        </Link>
        <TopTexts>
          <TopText>Shopping Bag({Items.length})</TopText>
        </TopTexts>
        <TopButton type="filled">CHECKOUT NOW</TopButton>
      </Top>
      <Wrapper>
        <Product>
          {Items.map((e) => {
            return (
              <Bottom>
                <Info>
                  <Image src={e.src} />
                  <ProductDetail>
                    <ProductName>
                      <b>Product:</b> {e.itemName}
                    </ProductName>
                    <br />
                    <ProductId>
                      <b>ID:</b> 93813718293
                    </ProductId>
                    <br />
                    <ProductSize>
                      <b>Size:</b> S
                    </ProductSize>
                  </ProductDetail>
                </Info>
              </Bottom>
            );
          })}
        </Product>
        <Summary>
          <SummaryTitle>ORDER SUMMARY</SummaryTitle>
          <SummaryItem>
            <SummaryItemText>Subtotal</SummaryItemText>
            <SummaryItemPrice>{Total}</SummaryItemPrice>
          </SummaryItem>
          <SummaryItem>
            <SummaryItemText>Estimated Shipping</SummaryItemText>
            <SummaryItemPrice>₹{ship}</SummaryItemPrice>
          </SummaryItem>
          <SummaryItem type="total">
            <SummaryItemText>Total</SummaryItemText>
            <SummaryItemPrice>₹{Total + ship}</SummaryItemPrice>
          </SummaryItem>
          <Button>CHECKOUT NOW</Button>
        </Summary>
      </Wrapper>
      <Newsletter />
      <Footer />
    </Container>
  );
};

export default Cart;
